// number of digits; 0 means just use as many as we need
void vgatext_decuns(vgatext *self, int32_t val, int32_t digits)
{
  vgatext_num(self, val, 10, 0, digits);
}

// print a hex number with the specified number
// of digits; 0 means just use as many as we need
void vgatext_hex(vgatext *self, int32_t val, int32_t digits)
{
  int32_t 	mask;
  vgatext_num(self, val, 16, 0, digits);
}

// print a newline
void vgatext_nl(vgatext *self)
{
  vgatext_tx(self, 13);
  vgatext_tx(self, 10);
}

